    private void enrollStudentActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
    	String enrollStudID = enrollStudentStudIDTxt.getText();
    	String enrollStudCName = enrollStudentCNameTxt.getText();
    	String enrollStudCSection = enrollStudentCSectionTxt.getText();
    	
        String result = "";
        try{
        result = univClient.enroll(enrollStudID, enrollStudCName, enrollStudCSection);
        }catch(JMSException ex)
        {
        	result = "";
        }
        enrollStudentResultTxt.setText(result);
    }//GEN-LAST:event_jButton1ActionPerformed

