    private void createStudentActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_createStudentActionPerformed
        // TODO add your handling code here:
    	String fName = createStudentFNameTxt.getText();
    	String lName = createStudentLNameTxt.getText();
    	String studentID = createStudentStudIDTxt.getText();
    	String studentAddress = createStudentAddressTxt.getText();
    	String studentCity = createStudentCityTxt.getText();
    	String studentState = createStudentStateTxt.getText();
    	String studentZip = createStudentZipTxt.getText();
        
        String result = "";
        try{
        result = univClient.createStudent(studentID,fName,lName,studentAddress,studentCity,studentState,studentZip);
        }catch(JMSException ex)
        {
        	result = "";
        }
        createStudentResultTxt.setText(result);
    	
    }//GEN-LAST:event_createStudentActionPerformed

